mapper.select:
SELECT a.article_id, a.title, a.type, a.article_face, a.recommend, a.STATUS, a.published, a.sort, a.views, a.create_time, a.update_time, c.class_id, c.NAME, c.color, COUNT( cm.article_id ) AS comments, u.nickname FROM t_article a LEFT JOIN t_classfication c ON a.class_id = c.class_id LEFT JOIN t_comment cm ON a.article_id = cm.article_id INNER JOIN sys_user u on a.author_id = u.user_id
${ew.customSqlSegment}
GROUP BY (a.article_id)


FROM t_article a
INNER JOIN t_classfication c ON a.class_id = c.class_id

    INNER JOIN t_tag_article ta ON ta.article_id = a.article_id AND ta.tag_id = #{tagId}


    AND a.class_id = #{classId}

WHERE a.`status` = 2 AND a.published = 1
order by a.create_time desc
